import React, { useState, useCallback } from 'react';
import './newsletter-signup.styles.scss';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component.jsx';
import axios from 'axios';

const NewsletterSignUp = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = event => {
    event.preventDefault();
    setEmail('');
  };

  const handleChange = event => {
    const { name, value } = event.target;
    setEmail({ ...email, [name]: value });
  };

  const sendData = () => {
    axios({
      url: 'newsletterData',
      method: 'post',
      data: {
        email,
      },
    })
      .then(res => console.log('Newsletter email sent!'))
      .catch(error => console.log('Error: ', JSON.parse(error)));
  };

  return (
    <div className="newsletter">
      <div className="container newsletter-title">
        <h3>Sign Up To Our Newsletter</h3>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="container newsletter-email">
          <FormInput
            type="email"
            spellCheck="false"
            autoComplete="on"
            name="email"
            value={email}
            handleChange={handleChange}
            required
            label="Enter your email address..."
          />
        </div>
        <CustomButton
          newsletter
          type="submit"
          onClick={useCallback(() => sendData, [sendData])}
        >
          SUBSCRIBE
        </CustomButton>
      </form>
    </div>
  );
};

export default NewsletterSignUp;
