import React, { useState } from "react";
import "./newsletter-signup.styles.scss";
import FormInput from "../form-input/form-input.component";
import CustomButton from "../custom-button/custom-button.component.jsx";
import { saveEmail } from "../../firebase/firebase.utils.js";

const NewsletterSignUp = () => {
  const [subscriptionInfo, setSubscriptionInfo] = useState({
    name: "",
    email: ""
  });

  const { name, email } = subscriptionInfo;

  const handleSubmit = event => {
    event.preventDefault();
    setSubscriptionInfo({
      name: "",
      email: ""
    });
    saveEmail(name, email);
    alert("Thank You!");
  };

  const handleChange = event => {
    const { name, value } = event.target;
    setSubscriptionInfo({ ...email, [name]: value });
  };

  return (
    <div className="newsletter">
      <div className="container newsletter-title">
        <h3>Sign Up To Our Newsletter</h3>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="container newsletter-email">
          <FormInput
            type="text"
            spellCheck="false"
            autoComplete="on"
            name="name"
            value={name}
            handleChange={handleChange}
            required
            label="Enter your email address..."
          />
          <FormInput
            type="email"
            spellCheck="false"
            autoComplete="on"
            name="email"
            value={email}
            handleChange={handleChange}
            required
            label="Enter your email address..."
          />
        </div>
        <CustomButton newsletter type="submit">
          SUBSCRIBE
        </CustomButton>
      </form>
    </div>
  );
};

export default NewsletterSignUp;
