import styled from "styled-components";
import { Link } from "react-router-dom";

export const HeaderContainer = styled.div`
  height: 70px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  margin-bottom: 25px;

  @media screen and (max-width: 800px) {
    height: 60px;
    padding: 10px;
    margin-bottom: 20px;
    width: 90%;
  }

  @media screen and (max-width: 900px) {
    height: 60px;
    padding: 10px;
    margin-bottom: 20px;
    width: 100%;
  }
`;

export const LogoContainer = styled(Link)`
  height: 100%;
  width: 70px;
  padding: 25px;

  @media screen and (max-width: 900px) {
    width: 50px;
    padding: 0;
  }

  @media screen and (max-width: 900px) {
    width: 55px;
  }
`;

export const Logo = styled.img`
  position: relative;
  bottom: 9px;

  &:hover {
    opacity: 0.85;
  }

  @media screen and (max-width: 900px) {
    width: 122%;
    height: 122%;
    margin-top: 5px;
    top: 4px;
  }
`;

export const LogoText = styled(Link)`
  width: 50%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  font-family: "Pacifico", cursive;
  font-size: 2.5rem;
  text-align: center;
  position: relative;
  top: 15px;
  left: 35px;

  &:hover {
    opacity: 0.8;
  }

  @media screen and (max-width: 900px) {
    font-size: 2.5em;
    margin-left: -17.5px;
  }
`;

export const OptionsContainer = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-end;

  @media screen and (max-width: 800px) {
    width: 55%;
  }

  &:last-child {
    color: #000;

    &:hover {
      color: purple;
    }
  }
`;

export const OptionLink = styled(Link)`
  padding: 10px 15px;
  color: black;
  font-size: 1.25rem;
  position: relative;
  top: 17.5px;
  cursor: pointer;

  &:hover {
    font-weight: bold;
    color: #fff;
    background-color: #000;
    border-radius: 5px;
  }

  @media screen and (max-width: 800px) {
    /* font-size: 1em; */
  }
`;
